const q = (s) => `'${String(s).replace(/'/g, `'"'"'`)}'`;

const ip = env.get("PCS_TX_IP") || "192.168.1.140";
const community = env.get("PCS_SNMP_COMMUNITY") || "public";
const mibDir = env.get("PCS_MIB_DIR") || "/home/dn/WORK/FrontPanelController/FrontPanelFirmware-SAMD51/pcs-node-red";
const scriptPath = `${mibDir.replace(/\/$/, "")}/poll_pcs_snmp.sh`;

msg.payload = `${q(scriptPath)} ${q(ip)} ${q(community)} ${q(mibDir)}`;
return msg;

const keys = [
    "pcsDeviceObjectId",
    "txForwardPower",
    "txReflectedPower",
    "txPowerPercent",
    "txInternalTemperature",
    "txExternalTemperature",
    "txAlarmBits",
    "txPAConnected",
    "txAlarmCodeNow",
    "txAlarmCodeLatched",
    "txExciterVoltage",
    "txPAVoltage",
    "txPA2Voltage",
    "txExciterCurrent",
    "txPACurrent",
    "txAudioInputSource",
    "txAudioGain",
    "txVULeft",
    "txVURight",
    "txStereoChannels",
    "txFrequencykHz"
];

const lines = String(msg.payload || "")
    .split(/\r?\n/)
    .map((s) => s.trim())
    .filter(Boolean);

if (lines.length === 0) {
    node.warn("SNMP command returned empty stdout. Check 'SNMP stderr' and 'SNMP return code' debug nodes.");
    return null;
}

if (lines.length < keys.length) {
    node.error(`Expected ${keys.length} SNMP lines, got ${lines.length}`, msg);
    return null;
}

const raw = {};
keys.forEach((k, idx) => {
    raw[k] = String(lines[idx] || "").replace(/^"|"$/g, "");
});

const num = (v) => {
    const n = Number(v);
    return Number.isFinite(n) ? n : null;
};

const alarmMap = {
    0: "noAlarm",
    1: "extTemp",
    2: "highSWR",
    3: "intTemp",
    4: "highCurrent",
    5: "highVoltage",
    6: "noExciter"
};

const audioSourceMap = {
    0: "analogInput",
    1: "aesEbu",
    2: "i2s1",
    3: "i2s2"
};

const stereoMap = {
    0: "mpxLeft",
    1: "mpxRight",
    2: "stereo",
    3: "monoLeftPlusRight"
};

const txForwardPowerRaw = num(raw.txForwardPower);
const txReflectedPowerRaw = num(raw.txReflectedPower);
const txInternalTemperatureRaw = num(raw.txInternalTemperature);
const txExternalTemperatureRaw = num(raw.txExternalTemperature);
const txExciterVoltageRaw = num(raw.txExciterVoltage);
const txPAVoltageRaw = num(raw.txPAVoltage);
const txPA2VoltageRaw = num(raw.txPA2Voltage);
const txExciterCurrentRaw = num(raw.txExciterCurrent);
const txPACurrentRaw = num(raw.txPACurrent);
const txAudioGainRaw = num(raw.txAudioGain);
const txAlarmBits = num(raw.txAlarmBits);
const txAlarmCodeNow = num(raw.txAlarmCodeNow);
const txAlarmCodeLatched = num(raw.txAlarmCodeLatched);
const txAudioInputSource = num(raw.txAudioInputSource);
const txStereoChannels = num(raw.txStereoChannels);
const txFrequencykHz = num(raw.txFrequencykHz);

msg.topic = "pcs/snmp/telemetry";
msg.payload = {
    timestamp: new Date().toISOString(),
    source: {
        ip: env.get("PCS_TX_IP"),
        community: env.get("PCS_SNMP_COMMUNITY")
    },
    raw,
    device: {
        objectId: raw.pcsDeviceObjectId
    },
    rf: {
        frequencykHz: txFrequencykHz,
        frequencyMHz: txFrequencykHz == null ? null : txFrequencykHz / 1000,
        forwardPowerRaw: txForwardPowerRaw,
        forwardPowerW: txForwardPowerRaw == null ? null : txForwardPowerRaw / 10,
        reflectedPowerRaw: txReflectedPowerRaw,
        reflectedPowerW: txReflectedPowerRaw == null ? null : txReflectedPowerRaw / 10,
        powerPercent: num(raw.txPowerPercent)
    },
    temperature: {
        internalRaw: txInternalTemperatureRaw,
        internalC: txInternalTemperatureRaw == null ? null : txInternalTemperatureRaw / 10,
        externalRaw: txExternalTemperatureRaw,
        externalC: txExternalTemperatureRaw == null ? null : txExternalTemperatureRaw / 10
    },
    alarms: {
        bits: txAlarmBits,
        bitsBinary: txAlarmBits == null ? null : (txAlarmBits >>> 0).toString(2).padStart(8, "0"),
        paConnected: num(raw.txPAConnected),
        codeNow: txAlarmCodeNow,
        codeNowText: alarmMap[txAlarmCodeNow] || "unknown",
        codeLatched: txAlarmCodeLatched,
        codeLatchedText: alarmMap[txAlarmCodeLatched] || "unknown"
    },
    rails: {
        exciterVoltageRaw: txExciterVoltageRaw,
        exciterVoltageV: txExciterVoltageRaw == null ? null : txExciterVoltageRaw / 10,
        paVoltageRaw: txPAVoltageRaw,
        paVoltageV: txPAVoltageRaw == null ? null : txPAVoltageRaw / 10,
        pa2VoltageRaw: txPA2VoltageRaw,
        pa2VoltageV: txPA2VoltageRaw == null ? null : txPA2VoltageRaw / 10,
        exciterCurrentRaw: txExciterCurrentRaw,
        exciterCurrentA: txExciterCurrentRaw == null ? null : txExciterCurrentRaw / 10,
        paCurrentRaw: txPACurrentRaw,
        paCurrentA: txPACurrentRaw == null ? null : txPACurrentRaw / 10
    },
    audio: {
        inputSource: txAudioInputSource,
        inputSourceText: audioSourceMap[txAudioInputSource] || "unknown",
        gainRaw: txAudioGainRaw,
        gainDb: txAudioGainRaw == null ? null : txAudioGainRaw / 10,
        vuLeft: num(raw.txVULeft),
        vuRight: num(raw.txVURight),
        stereoChannels: txStereoChannels,
        stereoChannelsText: stereoMap[txStereoChannels] || "unknown"
    }
};

return msg;

const p = msg.payload || {};
const rf = p.rf || {};
const t = p.temperature || {};
const a = p.alarms || {};
const audio = p.audio || {};
const rails = p.rails || {};

const isNum = (v) => Number.isFinite(v);
const fmt = (v, digits = 1, suffix = '') => isNum(v) ? Number(v).toFixed(digits) + suffix : '-';

const railsText = [
  'EX ' + fmt(rails.exciterVoltageV, 1, 'V') + ' / ' + fmt(rails.exciterCurrentA, 1, 'A'),
  'PA ' + fmt(rails.paVoltageV, 1, 'V') + ' / ' + fmt(rails.paCurrentA, 1, 'A'),
  'PA2 ' + fmt(rails.pa2VoltageV, 1, 'V')
].join(' | ');

const out = new Array(18).fill(null);
out[0] = { payload: isNum(rf.frequencyMHz) ? rf.frequencyMHz.toFixed(1) + ' MHz' : '-' };
out[1] = { payload: isNum(rf.forwardPowerW) ? rf.forwardPowerW : 0 };
out[2] = { payload: isNum(rf.reflectedPowerW) ? rf.reflectedPowerW : 0 };
out[3] = { payload: isNum(rf.powerPercent) ? rf.powerPercent : 0 };
out[4] = { payload: isNum(t.internalC) ? t.internalC : 0 };
out[5] = { payload: isNum(t.externalC) ? t.externalC : 0 };
out[6] = { payload: a.codeNowText || '-' };
out[7] = { payload: a.codeLatchedText || '-' };
out[8] = { payload: a.bitsBinary || '-' };
out[9] = { payload: Number(a.paConnected) === 1 ? 'Yes' : 'No' };
out[10] = { payload: audio.inputSourceText || '-' };
out[11] = { payload: isNum(audio.gainDb) ? audio.gainDb.toFixed(1) + ' dB' : '-' };
out[12] = { payload: audio.stereoChannelsText || '-' };
out[13] = { payload: railsText };
out[14] = { topic: 'Forward W', payload: isNum(rf.forwardPowerW) ? rf.forwardPowerW : 0 };
out[15] = { topic: 'Reflected W', payload: isNum(rf.reflectedPowerW) ? rf.reflectedPowerW : 0 };
out[16] = { topic: 'VU Left %', payload: isNum(audio.vuLeft) ? (audio.vuLeft / 255) * 100 : 0 };
out[17] = { topic: 'VU Right %', payload: isNum(audio.vuRight) ? (audio.vuRight / 255) * 100 : 0 };

return out;
